import { Link } from "react-router-dom";

const PaginationSubpage = ({ totalPage, currenPage, rangePage, sub }) => {
  let mide = Math.ceil(rangePage / 2);
  let min = currenPage - mide + 1;
  let max = min + rangePage - 1;
  let pages = [];

  if (max >= totalPage) {
    console.log(max);
    max = totalPage;
    min = max - rangePage + 1 <= 1 ? 1 : max - rangePage + 1;
  }
  if (max > totalPage) {
    max = totalPage;
  }

  if (min <= 1) {
    min = 1;
    max = rangePage;
  }

  for (let i = min; i <= max; i++) {
    pages.push(i);
  }

  return (
    <>
      <div className="pagination-subpage-box">
        <button>prew</button>
        {pages.map((pagi, ix) => {
          return (
            <>
              <button key={ix}>
                <Link to={`/${sub}?page=${pagi}`}>{pagi}</Link>
              </button>
            </>
          );
        })}
        <button>next</button>
      </div>
    </>
  );
};

export default PaginationSubpage;
