import { useEffect } from "react";
import BannerHomeComponent from "../../Component/BannerComponent/BannerComponent";
import "./HomePage.css";
import {
  apiGetHone,
  apigetListHot,
  apigetListupdata,
} from "../../Reducer/apiReques";
import { useDispatch, useSelector } from "react-redux";

const HomePage = () => {
  const item = useSelector((state) => state.getHomeReducer.getHome.item);
  const dispatch = useDispatch();
  useEffect(() => {
    apiGetHone(dispatch);
  });
  return (
    <>
      <div className="home-wrapper">
        {/* banner */}
        <div className="banner-wrapper">
          <BannerHomeComponent limitBookHot={limitBookHot} />
        </div>
      </div>
    </>
  );
};

export default HomePage;
