const PaginationSubpage = ({ totalPage, currenPage, rangePage }) => {
  let mide = Math.ceil(rangePage / 2);
  let min = currenPage - mide + 1;
  let max = min + rangePage - 1;
  let pages = [];
  for (let i = min; i <= max; i++) {
    if (i !== 0) {
      pages.push(i);
    }
  }

  return (
    <>
      <div className="pagination-subpage-box">
        <button>prew</button>
        {pages.map((pagi, ix) => {
          return (
            <>
              <button key={ix}>
                <Link>{pagi}</Link>
              </button>
            </>
          );
        })}
        <button>next</button>
      </div>
    </>
  );
};

export default PaginationSubpage;
