  let max = min + rangePage - 1;
  let pages = [];

  if (max >= totalPage) {
    max = totalPage;
    min = max - rangePage + 1 <= 1 ? 1 : max - rangePage + 1;
  }

  if (min <= 1) {
    min = 1;
    max = rangePage;
  }
