import { createSlice } from "@reduxjs/toolkit";

export const getlistBookupdataSlice = createSlice({
  name: "listBookupdata",
  initialState: {
    listBookupdata: {
      isfetching: false,
      item: null,
      error: false,
    },
  },
  reducers: {
    listBookupdataStart: (state) => {
      state.listBookupdata.isfetching = true;
    },
    listBookupdataSuccess: (state, action) => {
      state.listBookupdata.isfetching = false;
      state.listBookupdata.item = action.payload;
      state.listBookupdata.error = false;
    },
    listBookupdataFault: (state) => {
      state.listBookupdata.isfetching = false;
      state.listBookupdata.error = true;
    },
  },
});

export const {
  listBookupdataStart,
  listBookupdataSuccess,
  listBookupdataFault,
} = getlistBookupdataSlice.actions;

export default getlistBookupdataSlice.reducer;
