import { useEffect } from "react";
import BannerHomeComponent from "../../Component/BannerComponent/BannerComponent";
import "./HomePage.css";
import { apigetListHot } from "../../Reducer/apiReques";
import { useDispatch, useSelector } from "react-redux";

const HomePage = () => {
  const item = useSelector((state) => state.getListReducer.list.item);
  const lisbookHot = item?.listBooks;
  const limitBookHot = lisbookHot.slice(0, 16);
  const dispatch = useDispatch();
  useEffect(() => {
    apigetListHot(dispatch);
  }, []);
  return (
    <>
      <div className="home-wrapper">
        {/* banner */}
        <div className="banner-wrapper">
          {limitBookHot.map((item, index) => {
            <BannerHomeComponent />;
          })}
        </div>
      </div>
    </>
  );
};

export default HomePage;
