import { Col, Container, Row } from "react-bootstrap";
import "./BannerComponent.css";
import { Link } from "react-router-dom";
const BannerHomeComponent = ({ limitBookHot }) => {
  return (
    <>
      <Container>
        <Row>
          <div className="banner-box">
            <div className="title-home">
              <h6>
                <i class="fa-solid fa-fire-flame-curved pe-2 "></i>
                Truyện Hot
              </h6>
            </div>
            <Col xl={11}>
              <div className="banner-left">
                {limitBookHot.map((itemBookHot, idx) => {
                  return (
                    <Link>
                      <div className="book-box">
                        <img src={itemBookHot.dataImage} alt="" />
                        <div className="title-book">
                          <h4>
                            MAU XUYÊN NGHỊCH TẬP: BOSS THẦN BÍ, ĐỪNG TRÊU CHỌC
                            LUNG TUNG
                          </h4>
                        </div>
                      </div>
                    </Link>
                  );
                })}
              </div>
            </Col>
          </div>
        </Row>
      </Container>
    </>
  );
};

export default BannerHomeComponent;
