import { createSlice } from "@reduxjs/toolkit";

export const getListBookHotSlice = createSlice({
  name: "listBookHot",
  initialState: {
    list: {
      isfetching: false,
      item: null,
      error: false,
    },
  },
  reducers: {
    listHotStart: (state) => {
      state.list.isfetching = true;
    },
    listHotSuccess: (state, action) => {
      state.list.isfetching = false;
      state.list.item = action.payload;
      state.list.error = false;
    },
    listHotFault: (state) => {
      state.list.isfetching = false;
      state.list.error = true;
    },
  },
});

export const { listHotStart, listHotSuccess, listHotFault } =
  getListSlice.actions;

export default getListSlice.reducer;
