import { listHotFault, listHotStart, listHotSuccess } from "./getListSilice";
import axios from "axios";
export const apigetListHot = async (dispatch) => {
  dispatch(listHotStart());
  try {
    const resp = await axios.get("/danh-sach/truyen-hot?page=1");
    dispatch(listHotSuccess(resp.data.slice(0, 15)));
  } catch (error) {
    console.error(error);
    dispatch(listHotFault(error.response.data));
  }
};
