import { useEffect } from "react";
import BannerHomeComponent from "../../Component/BannerComponent/BannerComponent";
import "./HomePage.css";
import { apigetListHot } from "../../Reducer/apiReques";
import { useDispatch, useSelector } from "react-redux";

const HomePage = () => {
  const item = useSelector(
    (state) => state.getListBookHotReducer.listBokHot.item
  );
  // const lisbookHot = item?.listBooks;
  // const limitBookHot = lisbookHot.slice(0, 16);
  const dispatch = useDispatch();
  useEffect(() => {
    apigetListHot(dispatch);
  }, [dispatch]);
  return (
    <>
      <div className="home-wrapper">
        {/* banner */}
        <div className="banner-wrapper">{/* <BannerHomeComponent  /> */}</div>
      </div>
    </>
  );
};

export default HomePage;
