import { createSlice } from "@reduxjs/toolkit";

export const getListBookHotSlice = createSlice({
  name: "listBookHot",
  initialState: {
    listBookHot: {
      isfetching: false,
      item: null,
      error: false,
    },
  },
  reducers: {
    listBookHotStart: (state) => {
      state.listBookHot.isfetching = true;
    },
    listBookHotSuccess: (state, action) => {
      state.listBookHot.isfetching = false;
      state.listBookHot.item = action.payload;
      state.listBookHot.error = false;
    },
    listBookHotFault: (state) => {
      state.listBookHot.isfetching = false;
      state.listBookHot.error = true;
    },
  },
});

export const { listBookHotStart, listBookHotSuccess, listBookHotFault } =
  getListBookHotSlice.actions;

export default getListBookHotSlice.reducer;
