import { useEffect } from "react";
import BannerHomeComponent from "../../Component/BannerComponent/BannerComponent";
import "./HomePage.css";
import {
  apiGetHome,
  apigetListHot,
  apigetListupdata,
} from "../../Reducer/apiReques";
import { useDispatch, useSelector } from "react-redux";

const HomePage = () => {
  const itemd = useSelector(
    (state) => state.getListBookHotReducer.listBookHot.item
  );
  const lisbookHot = itemd?.listBooks;
  const limitBookHot = lisbookHot.slice(0, 16);
  const dispatch = useDispatch();

  useEffect(() => {
    apiGetHome(dispatch);
  }, [dispatch]);
  return (
    <>
      <div className="home-wrapper">
        {/* banner */}
        <div className="banner-wrapper">
          <BannerHomeComponent limitBookHot={limitBookHot} />
        </div>
      </div>
    </>
  );
};

export default HomePage;
