import { Col, Container, Row } from "react-bootstrap";
import "./BookHomeUpdata.css";
import { Link } from "react-router-dom";
const BookHomeUpdata = ({ newBook }) => {
  return (
    <>
      <Container>
        {newBook?.map((item, i) => {
          return (
            <>
              <Col xl={4}>
                <Link>
                  <div className="title-home-new">
                    <span>{item.title}</span>
                  </div>
                </Link>
              </Col>
              <Col xl={3}>
                <div className="genre-home">
                  {item.genres.map((data, i) => {
                    return (
                      <>
                        <Link>{data.genre} </Link>
                      </>
                    );
                  })}
                </div>
              </Col>
              <Col xl={2}>{item.chapters.chapter}</Col>
              <Col xl={2}>{item.time}</Col>
            </>
          );
        })}
      </Container>
    </>
  );
};

export default BookHomeUpdata;
