import axios from "axios";

import {
  listBookHotFault,
  listBookHotStart,
  listBookHotSuccess,
} from "./getListBookHotSilice";

import {
  listBookupdataFault,
  listBookupdataStart,
  listBookupdataSuccess,
} from "./getListBookUpdata";
import { getHomeFault, getHomeStart, getHomeSuccess } from "./getHomeSlice";

export const apigetListHot = async (dispatch) => {
  dispatch(listBookHotStart());
  try {
    const resp = await axios.get("/danh-sach/truyen-hot?page=1");
    dispatch(listBookHotSuccess(resp.data));
  } catch (error) {
    console.error(error);
    dispatch(listBookHotFault(error));
  }
};

export const apigetListupdata = async (dispatch) => {
  dispatch(listBookupdataStart());
  try {
    const resp = await axios.get("/danh-sach/truyen-moi?page=1");
    dispatch(listBookupdataSuccess(resp.data));
  } catch (error) {
    console.error(error);
    dispatch(listBookupdataFault(error));
  }
};

export const apiGetHome = async (dispatch) => {
  dispatch(getHomeStart());
  try {
    let reps = await axios.get("/");
    dispatch(getHomeSuccess(reps.data));
  } catch (error) {
    dispatch(getHomeFault(error));
    console.log(error);
  }
};
